-- ----------------------------
-- Table structure for equip
-- ----------------------------
DROP TABLE IF EXISTS `equip`;
CREATE TABLE `equip` (
  `equip_id` int(11) NOT NULL,
  `name` varchar(255) DEFAULT NULL COMMENT '装备名称',
  `type` int(11) DEFAULT NULL COMMENT '装备类型',
  `sale_price` int(11) DEFAULT NULL COMMENT '出售价格',
  `total_price` int(11) DEFAULT NULL COMMENT '购买价格',
  `des1` varchar(255) DEFAULT NULL COMMENT '装备描述1',
  `des2` varchar(255) DEFAULT NULL COMMENT '装备描述2',
  `img_url` varchar(255) DEFAULT NULL COMMENT '图片地址',
  PRIMARY KEY (`equip_id`),
  KEY `type` (`type`),
  CONSTRAINT `type` FOREIGN KEY (`type`) REFERENCES `equip_type` (`type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='装备';

