INSERT INTO `equip` VALUES ('1128', '冰霜长矛', '1', '1188', '1980', '<p>+80物理攻击<br>+600最大生命 </p>', '<p>唯一被动-碎冰：普通攻击会减少目标30%攻击速度和移动速度，持续2秒<br>远程英雄使用时减速效果持续时间衰减为1秒</p>', 'http://game.gtimg.cn/images/yxzj/img201606/itemimg/1128.jpg');
