INSERT INTO `equip` VALUES ('1134', '宗师之力', '1', '1260', '2100', '<p>+60物理攻击<br>+20%暴击率<br>+400 最大法力<br>+400最大生命</p>', '<p>唯一被动-强击：使用技能后，2秒内提升自身8%移动速度，并使得下次普通攻击造成额外1.0*物理攻击的物理伤害，冷却时间：2秒</p>', 'http://game.gtimg.cn/images/yxzj/img201606/itemimg/1134.jpg');
INSERT INTO `equip` VALUES ('1135', '闪电匕首', '1', '1104', '1840', '<p>+30%攻击速度<br>+20%暴击率<br>+8%移速</p>', '<p>唯一被动-电弧：普通攻击有30%几率释放连锁闪电，对目标造成（100+0.3AD）点法术伤害（该效果有0.5秒CD），这个伤害可以暴击</p>', 'http://game.gtimg.cn/images/yxzj/img201606/itemimg/1135.jpg');
